mapper.select:
select

from tb_charge_info ci
left join tb_consign_detail cd on cd.consign_detail_id = ci.consign_detail_id
left join tb_consign c on cd.consign_id = c.consign_id
left join tb_despatch_plan dp on cd.despatch_plan_id = dp.despatch_plan_id
left join td_record_info r on dp.record_id = r.record_id


		and c.ORG_ID = #{conditiions.orgId,jdbcType=VARCHAR}


		and  ci.charge_type = #{conditiions.chargeType,jdbcType=VARCHAR}


		and  c.CONSIGN_ORG_NAME REGEXP #{conditiions.consignOrgName,jdbcType=VARCHAR}


		and  r.CARRIER_NAME REGEXP #{conditiions.carrierName,jdbcType=VARCHAR}


		and  r.TRANSPORT_TOOL REGEXP #{conditiions.transportTool,jdbcType=VARCHAR}


		and ci.charge_fstate = #{conditiions.chargeFstate,jdbcType=VARCHAR}


		and  date_format(cd.DESPATCH_DATE,'%Y-%m-%d') >= #{conditiions.dispatchDateStart,jdbcType=VARCHAR} 


		and  date_format(cd.DESPATCH_DATE,'%Y-%m-%d') <=  #{conditiions.createDateEnd,jdbcType=VARCHAR} 


order by cd.DESPATCH_DATE,'%Y-%m-%d %T' desc

mapper.insert:
insert into tb_charge_info(charge_id,org_id,charge_type,consign_detail_id, despatch_plan_id,contract_id,FREIGHT_RATES,charge_fstate,MODIFY_USER_ID, MODIFY_DATE,MODIFY_USER_NAME) select cc.charge_id,#{despatchPlan.orgId, jdbcType=VARCHAR},cc.charge_type, cc.consign_detail_id,cc.despatch_plan_id,cc.contract_id,cc.FREIGHT_RATES, '00',#{despatchPlan.modifyUserId, jdbcType=VARCHAR},now(),#{despatchPlan.modifyUserName, jdbcType=VARCHAR} from (SELECT replace(uuid(),'-','') as "charge_id", case when r.carrier_type = '1' then '03' when r.carrier_type = '2' then '02' end as "charge_type", cd.consign_detail_id, dp.despatch_plan_id, ct.contract_id, ct.FREIGHT_RATES from tb_consign_detail cd left join tb_consign c on cd.consign_id = c.consign_id left join tb_despatch_plan dp on cd.despatch_plan_id = dp.despatch_plan_id left join td_record_info r on dp.record_id = r.record_id left join ( select con.contract_id,con.FREIGHT_RATES,cd.consign_detail_id from tb_contract con,tb_consign_detail cd, tb_consign c,tb_despatch_plan dp,td_record_info r where con.contract_type='02' and c.ORG_ID = con.org_id and cd.despatch_plan_id = dp.despatch_plan_id and dp.record_id = r.record_id and con.carrier_name = r.carrier_name and c.LOCATION_FROM = con.LOCATION_FROM and c.LOCATION_TO = con.LOCATION_TO and (cd.car_brand = con.car_brand or con.car_brand is null) and (cd.car_model = con.car_model or con.car_model is null) and cd.DESPATCH_DATE between con.CONTRACT_START and con.CONTRACT_END order by con.modify_date desc limit 0,1 ) ct on ct.consign_detail_id = cd.consign_detail_id where cd.consign_detail_id in
#{consignDetailIds[${index}], jdbcType=VARCHAR}
) cc

insert into tb_charge_info(charge_id,org_id,charge_type,consign_detail_id, despatch_plan_id,contract_id,FREIGHT_RATES,charge_fstate,MODIFY_USER_ID, MODIFY_DATE,MODIFY_USER_NAME) select cc.charge_id,#{despatchPlan.orgId, jdbcType=VARCHAR},cc.charge_type, cc.consign_detail_id,cc.despatch_plan_id,cc.contract_id,cc.FREIGHT_RATES, '00',#{despatchPlan.modifyUserId, jdbcType=VARCHAR},now(),#{despatchPlan.modifyUserName, jdbcType=VARCHAR} from (SELECT replace(uuid(),'-','') as "charge_id", '01' as "charge_type", cd.consign_detail_id, cd.despatch_plan_id, ct.contract_id, ct.FREIGHT_RATES from tb_consign_detail cd left join ( select con.contract_id,con.FREIGHT_RATES,cd.consign_detail_id from tb_contract con,tb_consign_detail cd, tb_consign c where con.contract_type='01' and c.ORG_ID = con.org_id and con.CONSIGN_ORG_NAME = c.CONSIGN_ORG_NAME and c.LOCATION_FROM = con.LOCATION_FROM and c.LOCATION_TO = con.LOCATION_TO and (cd.car_brand = con.car_brand or con.car_brand is null) and (cd.car_model = con.car_model or con.car_model is null) and cd.DESPATCH_DATE between con.CONTRACT_START and con.CONTRACT_END order by con.modify_date desc limit 0,1 ) ct on ct.consign_detail_id = cd.consign_detail_id where cd.consign_detail_id in
#{consignDetailIds[${index}], jdbcType=VARCHAR}
) cc

mapper.update:
update tb_charge_info set
   charge_fstate=

     when #{item.chargeId,jdbcType=VARCHAR} then #{item.chargeFstate,jdbcType=VARCHAR}

 ,MODIFY_USER_ID=

     when #{item.chargeId,jdbcType=VARCHAR} then #{item.modifyUserId,jdbcType=VARCHAR}

 ,MODIFY_USER_NAME=

     when #{item.chargeId,jdbcType=VARCHAR} then #{item.modifyUserName,jdbcType=VARCHAR}

 ,MODIFY_DATE=

     when #{item.chargeId,jdbcType=VARCHAR} then #{item.modifyDate,jdbcType=TIMESTAMP}

 where charge_id in

     #{item.chargeId,jdbcType=VARCHAR}

mapper.delete:
   	  delete from tb_charge_info
      where CONSIGN_DETAIL_ID in 

				#{consignDetailIds[${index}], jdbcType=VARCHAR}
